CREATE DATABASE HOSPITAL; 
USE HOSPITAL;   
CREATE TABLE DOCTOR ( PHONE_NUMBER varchar(10) DEFAULT NULL, NAME varchar(15) DEFAULT NULL, DEPARTMENT int DEFAULT NULL, SEX char(1) DEFAULT NULL, WORKING_HOURS varchar(25), DOCTOR_ID int NOT NULL,  PRIMARY KEY(DOCTOR_ID)  );
CREATE TABLE NURSE ( PHONE_NUMBER VARCHAR(10), NAME VARCHAR(30), SUPERVISOR_ID INT NOT NULL, PATIENT_ID INT NOT NULL, WORKING_HOURS VARCHAR(20), NURSE_ID INT NOT NULL, PRIMARY KEY(NURSE_ID), FOREIGN KEY(SUPERVISOR_ID) REFERENCES DOCTOR(DOCTOR_ID)  );
CREATE TABLE ROOM ( ROOM_NUMBER INT NOT NULL, PRIMARY KEY(ROOM_NUMBER)  );
CREATE TABLE PATIENT( PATIENT_ID INT NOT NULL, DATE_OF_BIRTH DATE, NAME VARCHAR(30), SEX CHAR(1), EMAIL VARCHAR(30), PHONE_NUMBER VARCHAR(10), ROOM_NUMBER INT NOT NULL, DOCTOR_ID INT NOT NULL, NURSE_ID INT NOT NULL, PRIMARY KEY(PATIENT_ID), FOREIGN KEY(DOCTOR_ID) REFERENCES DOCTOR(DOCTOR_ID), FOREIGN KEY(NURSE_ID) REFERENCES NURSE(NURSE_ID),FOREIGN KEY (ROOM_NUMBER) REFERENCES ROOM(ROOM_NUMBER ) );
CREATE TABLE MEDICLAIM_POLICY ( PATIENT_ID INT NOT NULL, BANK_AFFILIATED varchar(30) , AMOUNT_COVERED INT, DATE_OF_LAST_RENEWAL DATE, FOREIGN KEY(PATIENT_ID) REFERENCES PATIENT(PATIENT_ID)  );
CREATE TABLE PATIENT_BILL  ( PATIENT_ID INT NOT NULL, ROOM_OF_PATIENT INT , BILL_AMOUNT INT, DOCTOR_DEPARTMENT INT , DAYS_OF_STAY INT, DOCTOR_ID INT NOT NULL, FOREIGN KEY(PATIENT_ID) REFERENCES PATIENT(PATIENT_ID)  );
CREATE TABLE ADMISSION ( PATIENT_ID INT NOT NULL, DOCTOR_ID INT NOT NULL, NURSE_ID INT NOT NULL, ROOM_NUMBER INT NOT NULL,  FOREIGN KEY(DOCTOR_ID) REFERENCES DOCTOR(DOCTOR_ID), FOREIGN KEY(NURSE_ID) REFERENCES NURSE(NURSE_ID),FOREIGN KEY (ROOM_NUMBER) REFERENCES ROOM(ROOM_NUMBER ), FOREIGN KEY(PATIENT_ID) REFERENCES PATIENT(PATIENT_ID) );
CREATE TABLE DOCTOR_PHONE_NUMBER ( DOCTOR_ID INT NOT NULL, PHONE_NUMBER VARCHAR(10), PRIMARY KEY (DOCTOR_ID,PHONE_NUMBER) );
CREATE TABLE NURSE_PHONE_NUMBER ( NURSE_ID INT NOT NULL, PHONE_NUMBER VARCHAR(10), PRIMARY KEY (NURSE_ID,PHONE_NUMBER) );
CREATE TABLE PATIENT_PHONE_NUMBER ( PATIENT_ID INT NOT NULL, PHONE_NUMBER VARCHAR(10), PRIMARY KEY (PATIENT_ID,PHONE_NUMBER), FOREIGN KEY(PATIENT_ID) REFERENCES PATIENT(PATIENT_ID) );
CREATE TABLE PATIENT_EMAIL_ID ( PATIENT_ID INT NOT NULL, EMAIL_ID VARCHAR(30), PRIMARY KEY (PATIENT_ID,EMAIL_ID), FOREIGN KEY(PATIENT_ID) REFERENCES PATIENT(PATIENT_ID));
CREATE TABLE SURGEON ( DOCTOR_ID INT NOT NULL, PRIMARY KEY(DOCTOR_ID), FOREIGN KEY(DOCTOR_ID) REFERENCES DOCTOR(DOCTOR_ID) );
CREATE TABLE PHYSICIAN ( DOCTOR_ID INT NOT NULL, PRIMARY KEY(DOCTOR_ID), FOREIGN KEY(DOCTOR_ID) REFERENCES DOCTOR(DOCTOR_ID) );
CREATE TABLE PATIENT_DISTRICT ( DISTRICT VARCHAR(30), PINCODE VARCHAR(10)  );
CREATE TABLE PATIENT_BILL_DISTRICT ( DISTRICT VARCHAR(30), PINCODE VARCHAR(10)  );